import React from "react";
import "./home.css";
import Post from "./Post";
import Detailpost from "./Detailpost";

class Home extends React.Component {
  postHandaler = e => {
    let id = e.target.id;
    console.log(id);
  };

  deleteHandaler = id => {
    const post = this.props.posts;
    const posts = post.splice(id, 1);
    this.setState({ posts });
  };

  detailPostHandaler = id => {
    const post = this.props.posts;
    const posts = post.filter(item => (item.id = id));
    return <Detailpost item={posts} />;
  };

  render() {
    return (
      <div className="postOuterBody">
        <div className="PostBody">
          {this.props.posts.map((item, index) => {
            return (
              <Post
                id={item.id}
                key={index}
                item={item}
                deleteHandaler={() => this.deleteHandaler}
                onclick={() => detailPostHandaler(item.id)}
              />
            );
          })}
        </div>
      </div>
    );
  }
}

export default Home;
