import React from "react";
import "./home.css";
import Post from "./Post";
import DetailPost from "./DetailPost";

class Home extends React.Component {
  postHandaler = e => {
    let id = e.target.id;
    console.log(id);
  };

  deleteHandaler = id => {
    const post = this.props.posts.posts;
    const posts = post.splice(id, 1);
    this.setState({ posts });
  };

  showPost = id => {
    const name = this.props.posts.posts;
    const item = name.filter(post => post.id === id);
    console.log(item);
  };
  getItem = () => {
    this.showPost();
  };

  render() {
    this.getItem();
    return (
      <div className="postOuterBody">
        <div className="PostBody">
          {this.props.posts.posts.map((item, index) => {
            return (
              <div>
                <div onClick={() => this.showPost(item.id)}>
                  <Post
                    id={item.id}
                    key={index}
                    item={item}
                    deleteHandaler={() => this.deleteHandaler}
                  />
                </div>
              </div>
            );
          })}
        </div>
        <DetailPost post={this.showPost()} />;
      </div>
    );
  }
}

export default Home;
