import React from "react";
import "./navigation.css";

class Addpost extends React.Component {
  state = [
    {
      id: "",
      title: "",
      category: "",
      details: ""
    }
  ];

  handleInput = e => {
    console.log(e.target.value);
  };
  saveInput = e => {};
  render() {
    return (
      <div className="nav">
        <form>
          <label>Title : </label>
          <input type="text" name="title" onChange={this.handleInput} />
          <br />
          <label>Category : </label>
          <input type="text" name="category" onChange={this.handleInput} />
          <br />
          <label>Write New Post : </label>
          <br />
          <textarea
            cols="100"
            rows="15"
            name="details"
            onChange={this.handleInput}
          />
        </form>
      </div>
    );
  }
}

export default Addpost;
