import React from "react";
import "./home.css";

class Home extends React.Component {
  postHandaler = e => {
    let id = e.target.id;
    console.log(id);

    const data = this.props.posts.filter(item => item.id === id);
    console.log(this.props.posts.map(item => item.id));
  };

  render() {
    return (
      <div className="postOuterBody">
        <div className="PostBody">
          {this.props.posts.map((item, index) => {
            return (
              <div
                id={item.id}
                key={index}
                onClick={this.postHandaler}
                className="post"
              >
                <p>{item.id}</p>
                <p>{item.title}</p>
                <p>{item.category}</p>
              </div>
            );
          })}
        </div>
      </div>
    );
  }
}

export default Home;
