import React from "react";
import "./home.css";

class Home extends React.Component {
  postHandaler = e => {
    let id = e.target.id;
    console.log(id);
  };

  render() {
    return (
      <div className="postOuterBody">
        <div className="PostBody">
          {this.props.posts.map(item, key => {
            return (
              <div
                id={this.props.posts.id}
                onClick={this.postHandaler}
                className="post"
              >
                <p>{this.props.posts.id}</p>
                <p>{item.title}</p>
                <p>{item.category}</p>
              </div>
            );
          })}
        </div>
      </div>
    );
  }
}

export default Home;
